mapper.insert:

	SELECT IFNULL(MAX(CLASS_ID), 0)
	FROM CLASS;

INSERT
INTO CLASS
VALUES (
	#{CLASS_ID} + 1
	, #{CLASS_TITLE}
	, #{MEM_ID}
	, #{CLASS_INTRO}
	, #{CLASS_PRICE}
	, #{CLASS_CATEGORY}
	, CURDATE()
	, #{CLASS_RUNTIME}
	, #{CLASS_PIC1}
	, #{CLASS_CONTENTS}
	, #{CLASS_STATUS});
