mapper.delete:
-- 관심목록 삭제
DELETE FROM WISHLIST
WHERE CLASS_ID = #{class_id};

mapper.select:
-- 나의 강의실 목록 조회 SELECT A.MEM_ID , A.COURSE_STATUS , IFNULL(A.STUDY_TIME, 0) AS STUDY_TIME , C.CLASS_ID , C.CLASS_CATEGORY , C.CLASS_TITLE , C.CLASS_RUNTIME , (SELECT COUNT(CUR_ID) FROM CURRICULUM WHERE CLASS_ID = C.CLASS_ID) AS CURRICULUM_COUNT , M.MEM_NAME AS TEACHER_NAME , R.REVIEW_IDX , R.REVIEW_CONTENT FROM MYCOURSE A LEFT JOIN CLASS C ON C.CLASS_ID = A.CLASS_ID LEFT JOIN MEMBER M ON C.MEM_ID = M.MEM_ID LEFT JOIN REVIEW R ON R.CLASS_ID = A.CLASS_ID AND R.MEM_ID = A.MEM_ID WHERE A.MEM_ID = #{id}
ORDER BY C.CLASS_REG_DATE DESC
ORDER BY C.CLASS_TITLE

mapper.insert:
-- 수강평 등록
INSERT 
INTO REVIEW
VALUES (
		null 				-- 번호 자동증가
		, #{mem_id}
	    , #{review_subject}
	    , #{review_content}
	    , NOW()
	    , #{class_id}
	    , #{review_score}
	    , null
	    , null
   		)

mapper.select:
	-- 수강 후기 작성 여부
	SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END AS IS_REVIEWED
      FROM REVIEW
     WHERE MEM_ID = #{id}
       AND CLASS_ID = #{class_id}

mapper.update:
	UPDATE REVIEW
	   SET
		   REVIEW_CONTENT = #{review_content}
    	   , REVIEW_DATE = NOW()
    	   , REVIEW_SCORE = #{review_score}
	 WHERE 
	 	   MEM_ID = #{mem_id}
	   AND CLASS_ID = #{class_id}
