mapper.insert:

	SELECT	IFNULL(MAX(notice_idx), 0)
	FROM	NOTICE

INSERT
INTO	NOTICE
VALUES (
	#{notice_idx} + 1
	, #{mem_id}
	, #{notice_subject}
	, #{notice_content}
	, NOW()
	, 1					--	notice_cafe
	, #{notice_file}	
	, 0					--	notice_read_count
	)

mapper.update:
UPDATE	NOTICE
SET		NOTICE_FILE = #{updatedFileList}
WHERE	NOTICE_IDX	= #{notice_idx}

		UPDATE	NOTICE AS N
		  JOIN	(
				SELECT	NOTICE_IDX, NOTICE_FILE
     	      	  FROM	NOTICE
    	     	 WHERE	NOTICE_IDX = #{notice_idx}
				) AS S
			ON	N.NOTICE_IDX = S.NOTICE_IDX
		   SET	N.NOTICE_FILE = CONCAT (
					IFNULL(N.NOTICE_FILE, '')
					, ','
					, IFNULL(#{fileName}, '')
				)
		WHERE	N.NOTICE_IDX = #{notice_idx}

UPDATE	NOTICE
SET		NOTICE_SUBJECT = #{notice_subject}
		, NOTICE_CONTENT = #{notice_content}
WHERE	NOTICE_IDX = #{notice_idx}

	
		SELECT	NOTICE_READ_COUNT
		FROM	NOTICE
		WHERE	NOTICE_IDX = #{notice_idx}
	

	UPDATE	NOTICE
	SET		NOTICE_READ_COUNT = NOTICE_READ_COUNT + 1
	WHERE	NOTICE_IDX = #{notice_idx}
