mapper.insert:
INSERT INTO PAY_INFO
( 	MERCHANT_UID
	, CLASS_NAME
	, MEM_ID
	, MEM_NAME
	, PRICE
	, DISCOUNT_AMOUNT
	, PAY_METHOD
	, PAY_STATUS
	, PAY_DATE
	, IMP_UID
	, CARD_NAME
	, CARD_NUM
	, BANK_NAME
	, BANK_NUM
	, RECEIPT_URL
)
VALUES 
(	#{merchant_uid}
	, #{class_name}
	, #{mem_id}
	, #{mem_name}
	, #{price}
	, #{discount_amount}
	, #{pay_method}
	, CASE
		WHEN #{pay_status} = 'paid' THEN 1		-- 결제완료
		WHEN #{pay_status} = 'failed' THEN 2	-- 결제취소
		WHEN #{pay_status} = 'ready' THEN 3		-- 결제대기(무통장입금) 
		ELSE NULL
	  END
	, CURRENT_TIMESTAMP()
	, #{imp_uid}
	, #{card_name}
	, #{card_num}
	, #{bank_name}
	, #{bank_num}
	, #{receipt_url}
)


	SELECT IFNULL(MAX(order_idx), 0)
	FROM ORDER_INFO

INSERT INTO ORDER_INFO
( 
	ORDER_IDX
	, MERCHANT_UID
	, MEM_ID
	, CLASS_ID
	, CLASS_PRICE
	, COUPON_ID
	, PAY_PRICE
)
VALUES
(
	#{order_idx} + 1
	, #{merchant_uid}
	, #{mem_id}
	, #{class_id}
	, #{class_price}
	, #{coupon_id}
	, #{price}
)

mapper.update:
UPDATE MYCOUPON
SET COUPON_ISUSED = 2
WHERE COUPON_ID = #{coupon_id}

mapper.select:
SELECT MEM_ID
FROM PAY_INFO
WHERE IMP_UID = #{imp_uid}

mapper.update:
UPDATE PAY_INFO
SET PAY_STATUS = 2
WHERE 1=1
AND MEM_ID = #{memId}  
AND	IMP_UID = #{imp_uid}

	UPDATE MYCOUPON
	SET COUPON_ISUSED = 2
	WHERE 1=1
	AND MEM_ID = #{memId} 
	AND COUPON_ID = (
		SELECT OI.COUPON_ID
		FROM PAY_INFO PI
		JOIN ORDER_INFO OI ON PI.MERCHANT_UID = OI.MERCHANT_UID
		WHERE PI.IMP_UID = #{imp_uid}
	);
